import { createSlice } from "@reduxjs/toolkit";

const uislice = createSlice({
    name:'ui',
    initialState:{carISEnabale:false},
    reducers:{
        toggle(state){
            state.carISEnabale=!state.carISEnabale
        }
    }
});
export const uiActions = uislice.actions;
export default uiActions;
